    private void bIgualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bIgualActionPerformed
        // TODO add your handling code here:
        segundoNumero = Double.parseDouble(tfCampo.getText().replace(',', '.'));
        if (operaciones.equals(bMas.getText())) {
            tfCampo.setText(((primerNumero + segundoNumero) + "").replace('.', ','));
        } else {
            if (operaciones.equals(bMenos.getText())) {
                tfCampo.setText(((primerNumero - segundoNumero) + "").replace('.', ','));
            } else {
                if (operaciones.equals(bPor.getText())) {
                    tfCampo.setText(((primerNumero * segundoNumero) + "").replace('.', ','));
                } else {
                    if (operaciones.equals(bDiv.getText())) {
                       
                            tfCampo.setText(((primerNumero / segundoNumero) + "").replace('.', ','));
                        
                        
                    }
                }
            }
        }
        primerNumero = 0;
        segundoNumero = 0;
        operaciones = "";
        
    }//GEN-LAST:event_bIgualActionPerformed

