    private void bConvertirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bConvertirActionPerformed
        // TODO add your handling code here:
        ca = new CalculadoraConversion(Double.parseDouble(tfCampoConver.getText().replace(',', '.')));
        String resultado = "";
        if(lOpciones.getSelectedItem().equals("Longitud")){
            resultado += "m -> in\t" + ca.MetroIn();
            resultado += "\nm -> cm\t" + ca.MetrosCm();
            resultado += "\nm -> Km\t" + ca.MetrosKm();
            resultado += "\nm -> yd\t" + ca.MetrosYd();
            resultado += "\nm -> ft\t" + ca.Metrosft();
            resultado += "\ncm -> m\t" + ca.CmMetros();
            resultado += "\ncm -> in\t" + ca.CmIn();
            resultado += "\ncm -> Km\t" + ca.CmKm();
            resultado += "\ncm -> yd\t" + ca.CmYd();
            resultado += "\ncm -> ft\t" + ca.CmFt();
            resultado += "\nKm -> m\t" + ca.KmMetro();
            resultado += "\nKm -> in\t" + ca.KmIn();
            resultado += "\nKm -> cm\t" + ca.KmCm();
            resultado += "\nKm -> yd\t" + ca.KmYd();
            resultado += "\nKm -> ft\t" + ca.Kmft();
            resultado += "\nin -> m\t" + ca.InMeTros();
            resultado += "\nin -> cm\t" + ca.Incm();
            resultado += "\nin -> Km\t" + ca.InKm();
            resultado += "\nin -> yd\t" + ca.InYd();
            resultado += "\nin -> ft\t" + ca.InFt();
            resultado += "\nft -> m\t" + ca.FtMetro();
            resultado += "\nft -> cm\t" + ca.FtCm();
            resultado += "\nft -> Km\t" + ca.FtKm();
            resultado += "\nft -> yd\t" + ca.FtYd();
            resultado += "\nft -> in\t" + ca.Ftin();
            resultado += "\nyd -> m\t" + ca.YdMetros();
            resultado += "\nyd -> cm\t" + ca.YdCm();
            resultado += "\nyd -> Km\t" + ca.YdKm();
            resultado += "\nyd -> ft\t" + ca.YdFt();
            resultado += "\nyd -> in\t" + ca.YdIn();
            
        } else {
            if (lOpciones.getSelectedItem().equals("Masa")) {
                resultado += "Kg -> g\t" + ca.KgG();
                resultado += "\nKg -> lb\t" + ca.KgLb();
                resultado += "\nKg -> t\t" + ca.KgT();
                resultado += "\ng -> Kg\t" + ca.GKg();
                resultado += "\ng -> lb\t" + ca.GLb();
                resultado += "\ng -> t\t" + ca.Gt();
                resultado += "\nt -> Kg\t" + ca.TKg();
                resultado += "\nt -> g\t" + ca.TG();
                resultado += "\nt -> lb\t" + ca.Tlb();
                
            } else {
                if (lOpciones.getSelectedItem().equals("Temperatura")) {
                    resultado += "°C -> °F\t" + ca.Celciusfahrenheit();
                    resultado += "\n°C -> K\t" + ca.CelciusKelvin();
                    resultado += "\n°F -> K\t" + ca.Fahrenheitkelvin();
                    resultado += "\n°F -> °C\t" + ca.FahrenheiCelcius();
                    resultado += "\nK -> °C\t" + ca.KelvinCelcius();
                    resultado += "\nK -> °F\t" + ca.KelvinFahrenheit();
                }
                
            }
        }
        taConv.setText(resultado);
        
    }//GEN-LAST:event_bConvertirActionPerformed

